n groove; }	'! !!PromoterSearchEngine methodsFor: 'actions' stamp: 'gsa 4/15/2004 18:47'!addNew	| newSearchEngine |	newSearchEngine := self				call: (MWEditorVisitor model: SearchEngine new).	newSearchEngine		ifNotNil: [self session domainmodel addSearchEngine: newSearchEngine.			self initialize]! !!PromoterSearchEngine methodsFor: 'actions' stamp: 'gsa 4/15/2004 18:50'!delete: aSearchEngine 	(self confirm: 'Do you really want to delete ' , aSearchEngine asString , '?')		ifTrue: [self session domainmodel removeSearchEngine: aSearchEngine.			self initialize]! !!PromoterSearchEngine methodsFor: 'actions' stamp: 'gsa 4/15/2004 18:51'!edit: aSearchEngine 	self		call: (MWEditorVisitor model: aSearchEngine)! !!PromoterSearchEngine class methodsFor: 'class initialization' stamp: 'gsa 4/15/2004 18:41'!initialize	self registerAsApplication: 'PromoterSearchEngine' sessionClass: PromoterMainSession! !!PromoterTask methodsFor: 'accessing' stamp: 'gsa 4/24/2004 12:03'!companyToPromote	^ companyToPromote! !!PromoterTask methodsFor: 'accessing' stamp: 'gsa 4/24/2004 12:12'!companyToPromote: anObject 	companyToPromote := anObject! !!PromoterTask methodsFor: 'accessing' stamp: 'gsa 4/25/2004 09:22'!metaobject	| metaobject |	metaobject := MWMetaobject for: self.	metaobject		singleRelationshipAttribute: #companyToPromote		do: [:attribute | attribute label: 'Company';								relationshipTo: [PromoterMainSession currentSession domainmodel companies]				formatWith: [:each | each coSiteName , ' (' , each coURL , ')'];				 nilItemString: 'unknown' translated].	metaobject		multipleRelationshipAttribute: #searchEnginesToUse		do: [:attribute | attribute label: 'Search Engines to Use' translated;								relationshipTo: [PromoterMainSession currentSession domainmodel searchEngines]				formatWith: [:eachEngine | eachEngine seName, ' (' , eachEngine seURL , ')'];								addValidationRule: [:collection | collection isEmpty not]				errorString