"	html h3: 'Promotion Tasks' translated.	html cssClass: 'TABLE-LIST'.	html		table: [batcher batch				do: [:viewer | html						tableRow: [html render: viewer.							html								tableData: [html										anchorWithAction: [self edit: viewer metaobject baseobject]										text: 'edit';										 space;																				anchorWithAction: [self delete: viewer metaobject baseobject]										text: 'delete';										 space;																				anchorWithAction: [self promot: viewer metaobject baseobject]										text: 'Promote!!']]].			html attributeAt: #align put: #center.			html tableRowWith: batcher span: 8].	html break.	html		anchorWithAction: [self addNew]		text: 'Add a Promotion Task'! !!PromoterTaskUI methodsFor: 'rendering' stamp: 'gsa 4/24/2004 11:28'!style	^ '		body { font-size : 12px; font-family : Arial;  }		.TABLE-LIST { width: 500; font-size: 12px; font-family: Arial; border: thin groove; }	'! !!PromoterTaskUI methodsFor: 'actions' stamp: 'gsa 4/24/2004 11:29'!addNew	| newPromoterTask |	newPromoterTask := self				call: (MWEditorVisitor model: PromoterTask new).	newPromoterTask		ifNotNil: [self session domainmodel addPromoterTask: newPromoterTask.			self initialize]! !!PromoterTaskUI methodsFor: 'actions' stamp: 'gsa 5/23/2004 15:02'!addURL: aCompany inSearchEngine: aEngine 	"Aquí armo el string de adicion a buscadores, según el  	buscador de que se trate"	"Google Argentina"	"TO-DO Write the complete method"	"aPromoterTask explore"	| url |	aEngine seURL = 'http://www.google.com.ar/addurl'		ifTrue: ["Build the submit string"			"self halt."			"Armar como se debe"			"httpString := aEngine seURL , '?q=' , aCompany  			coURL , '&dq=' , aCompany coDescription ,  			'&submit=Agregar+URL'."			"----------------------------------"			"Armado de prueba"			"httpString := aEngine seURL , '?q=' , aCompany  			coURL , '&dq=squeak&submit=Agregar+URL'."			"----------------------------------"			"Más pruebas"			url := 'http://localhost/test.htm'.			"aReturnPage := HTTPSocket httpGet: url accept: 'text/html'."			aReturnPage := HTTPSocket httpGet: url.			"aReturnPage := HTTPSocket httpShowPage:  			httpString."			"HTTPSocket httpGet: url accept: 'text/html'"			"HTTPSocket httpGet: url."			"----------------------------------"			"aReturnPage := HTTPSocket httpShowPage:  			httpString."			^ aReturnPage].	aEngine seURL = 'http://www.altavista.com'		ifTrue: [url := 'http://localhost/test1.htm'.			aReturnPage := HTTPSocket httpGet: url accept: 'text/html'.			^ aReturnPage].	aEngine seURL = 'http://www.yahoo.com.ar'		ifTrue: [url := 'http://localhost/test2.htm'.			aReturnPage := HTTPSocket httpGet: url accept: 'text/html'.			^ aReturnPage]! !!PromoterTaskUI methodsFor: 'actions' stamp: 'gsa 4/24/2004 11:31'!delete: aPromoterTask	(self confirm: 'Do you really want to delete ' , aPromoterTask asString , '?')		ifTrue: [self session domainmodel removePromoterTask: aPromoterTask.			self initialize]! !!PromoterTaskUI methodsFor: 'actions' stamp: 'gsa 4/24/2004 11:32'!edit: aPromoterTask 	self		call: (MWEditorVisitor model: aPromoterTask)! !!PromoterTaskUI methodsFor: 'actions' stamp: 'gsa 5/22/2004 17:44'!promot: aPromoterTask 	(self confirm: 'Do you really want to start the Promotion Task to 	' , aPromoterTask companyToPromote coSiteName asString , '?')		ifTrue: ["self session domainmodel promote: aPromoterTask."			self promote: aPromoterTask.			"self inform: aReturnPage."			"Con lo anterior intenté mostrar la salida del addurl en 			google, pero no funciona, supongo que porque  			aReturnPage vuelve a #promote del model"			self initialize]! !!PromoterTaskUI methodsFor: 'actions' stamp: 'gsa 5/23/2004 15:08'!promote: aPromoterTask 	"The method that add the data to the search Engines"	"TO-DO Write the complete method"	"aPromoterTask explore"	| sEngines aCompany |	aCompany := aPromoterTask companyToPromote.	sEngines := aPromoterTask searchEnginesToUse.	"sEngines  	do: [:aEngine | Transcript show: aEngine seURL printString;  	cr]"	sEngines		do: [:aEngine | 			self addURL: aCompany inSearchEngine: aEngine.			self inform: aReturnPage]! !!PromoterTaskUI class methodsFor: 'class initialization' stamp: 'gsa 4/24/2004 16:55'!initialize	self registerAsApplication: 'PromoterTaskUI' sessionClass: PromoterMainSession! !!SearchEngine methodsFor: 'accessing' stamp: 'gsa 4/25/2004 11:01'!metaobject	"Mewa required method"	| metaobject |	metaobject := MWMetaobject for: self.	metaobject		textAttribute: #seName		do: [:attribute | attribute label: 'Search Engine' translated;				 maxLength: 20;				 addRequiredRule].	metaobject		textAttribute: #seURL		do: [:attribute | attribute label: 'URL' translated;				 maxLength: 150;				 addRequiredRule].	metaobject		singleRelationshipAttribute: #seMethod		do: [:attribute | attribute label: 'Method' translated;								relationshipTo: [self class seMethods]].	"metaobject  	textAttribute: #seMethod  	do: [:attribute | attribute label: 'Method' translated;  	maxLength: 4;  	addRequiredRule]."	metaobject		textAttribute: #seSuccessString		do: [:attribute | attribute label: 'Success String' translated;				 maxLength: 30;				 addRequiredRule].	metaobject		textAttribute: #seUserAgent		do: [:attribute | attribute label: 'User Agent' translated;				 maxLength: 50;				 addRequiredRule].	metaobject		textAttribute: #seReferer		do: [:attribute | attribute label: 'Referer' translated;				 maxLength: 50;				 addRequiredRule].	^ metaobject! !!SearchEngine methodsFor: 'accessing' stamp: 'gsa 4/15/2004 18:56'!seMethod	^ seMethod! !!SearchEngine methodsFor: 'accessing' stamp: 'gsa 4/4/2004 10:16'!seMethod: anObject	seMethod _ anObject! !!SearchEngine methodsFor: 'accessing' stamp: 'gsa 4/15/2004 18:54'!seName	^ seName! !!SearchEngine methodsFor: 'accessing' stamp: 'gsa 4/4/2004 10:04'!seName: anObject	seName _ anObject! !!SearchEngine methodsFor: 'accessing' stamp: 'gsa 4/15/2004 18:56'!seReferer	^ seReferer! !!SearchEngine methodsFor: 'accessing' stamp: 'gsa 4/4/2004 10:16'!seReferer: anObject	seReferer _ anObject! !!SearchEngine methodsFor: 'accessing' stamp: 'gsa 4/15/2004 18:56'!seSuccessString	^ seSuccessString! !!SearchEngine methodsFor: 'accessing' stamp: 'gsa 4/4/2004 10:16'!seSuccessString: anObject	seSuccessString _ anObject! !!SearchEngine methodsFor: 'accessing' stamp: 'gsa 4/15/2004 18:56'!seURL	^ seURL! !!SearchEngine methodsFor: 'accessing' stamp: 'gsa 4/4/2004 10:05'!seURL: anObject	seURL _ anObject! !!SearchEngine methodsFor: 'accessing' stamp: 'gsa 4/15/2004 18:56'!seUserAgent	^ seUserAgent! !!SearchEngine methodsFor: 'accessing' stamp: 'gsa 4/4/2004 10:16'!seUserAgent: anObject	seUserAgent _ anObject! !!SearchEngine class methodsFor: 'accessing' stamp: 'gsa 4/25/2004 10:54'!seMethods	^ #(#GET #POST)! !PromoterTaskUI initialize!PromoterSearchEngine initialize!PromoterMain initialize!PromoterInfo initialize!PromoterCompany initialize!